import{_ as e,c as t,a0 as i,o as l}from"./chunks/framework.Bhj1ewZm.js";const m=JSON.parse('{"title":"Erfan Jap - Skills","description":"","frontmatter":{},"headers":[],"relativePath":"skills.md","filePath":"skills.md"}'),o={name:"skills.md"};function r(s,a,n,h,c,u){return l(),t("div",null,a[0]||(a[0]=[i('<h1 id="erfan-jap-skills" tabindex="-1">Erfan Jap - Skills <a class="header-anchor" href="#erfan-jap-skills" aria-label="Permalink to &quot;Erfan Jap - Skills&quot;">​</a></h1><h2 id="_2024" tabindex="-1">2024 <a class="header-anchor" href="#_2024" aria-label="Permalink to &quot;2024&quot;">​</a></h2><h3 id="batch-processing-system-for-aba-file-format" tabindex="-1">Batch processing system for ABA file format <a class="header-anchor" href="#batch-processing-system-for-aba-file-format" aria-label="Permalink to &quot;Batch processing system for ABA file format&quot;">​</a></h3><p>Design and development of a batch processing system for ABA files.</p><p>ABA (Australian Bankers Association) file format is a universal format used by banks in Australia to allow for batch transactions.</p><p>The system will facilitate near-real-time payments via the NPP (<a href="https://www.rba.gov.au/payments-and-infrastructure/new-payments-platform/" target="_blank" rel="noreferrer">New Payments Platform</a>) or, if unavailable, through the <a href="https://www.auspaynet.com.au/resources/direct-entry" target="_blank" rel="noreferrer">Direct Entry</a> system.</p><h4 id="tech-stack" tabindex="-1">Tech stack <a class="header-anchor" href="#tech-stack" aria-label="Permalink to &quot;Tech stack&quot;">​</a></h4><ul><li>Batch processing system <ul><li>AWS Lambda, DynamoDB, SQS</li><li>AWS S3 and Transfer Family (for SFTP/Secure File Transfer Service)</li><li>AWS EventBridge for scheduling</li></ul></li><li>CI/CD <ul><li>AWS CloudFormation</li><li>AWS CloudWatch for monitoring</li><li>GitLab CI/CD</li></ul></li><li>Automated Testing <ul><li>Playwright</li></ul></li></ul><h3 id="checkout-app" tabindex="-1">Checkout app <a class="header-anchor" href="#checkout-app" aria-label="Permalink to &quot;Checkout app&quot;">​</a></h3><p>Developed a Checkout application enabling users to make payments for products or services.</p><p>Payments are processed through the <a href="https://payto.com.au" target="_blank" rel="noreferrer">PayTo</a> infrastructure.</p><h4 id="tech-stack-1" tabindex="-1">Tech stack <a class="header-anchor" href="#tech-stack-1" aria-label="Permalink to &quot;Tech stack&quot;">​</a></h4><ul><li>Frontend <ul><li>Vue.js</li><li>Storybook</li></ul></li><li>Backend <ul><li>AWS AppSync with GraphQL subscriptions for real-time data app</li><li>AWS Lambda, DynamoDB, SQS</li></ul></li><li>CI/CD <ul><li>AWS CDK</li><li>AWS CloudWatch for monitoring</li><li>GitLab CI/CD</li></ul></li><li>Automated Testing <ul><li>Playwright</li></ul></li></ul><h3 id="migration-of-vue-2-to-vue-3" tabindex="-1">Migration of Vue 2 to Vue 3 <a class="header-anchor" href="#migration-of-vue-2-to-vue-3" aria-label="Permalink to &quot;Migration of Vue 2 to Vue 3&quot;">​</a></h3><p>Migration of Vue 2 project (As Vue 2 is EOL) to Vue 3.</p><p>As part of the migration, also migrating the following:</p><ul><li>Webpack to vite</li><li>Jest to vitest</li><li>WebdriverIO to Playwright</li></ul><h3 id="cloudwatch-metric-dashboard-and-alarm" tabindex="-1">CloudWatch Metric, Dashboard, and Alarm <a class="header-anchor" href="#cloudwatch-metric-dashboard-and-alarm" aria-label="Permalink to &quot;CloudWatch Metric, Dashboard, and Alarm&quot;">​</a></h3><p>Automate the creation of CloudWatch dashboards and alarms to monitor application health and performance.</p><p>As part of the project, also automate the creation of CloudWatch custom metrics.</p><h4 id="tech-stack-2" tabindex="-1">Tech stack <a class="header-anchor" href="#tech-stack-2" aria-label="Permalink to &quot;Tech stack&quot;">​</a></h4><ul><li>AWS CloudWatch</li><li>AWS CloudFormation</li></ul><h2 id="_2023" tabindex="-1">2023 <a class="header-anchor" href="#_2023" aria-label="Permalink to &quot;2023&quot;">​</a></h2><h3 id="user-dashboard" tabindex="-1">User Dashboard <a class="header-anchor" href="#user-dashboard" aria-label="Permalink to &quot;User Dashboard&quot;">​</a></h3><p>Development of new features for user dashboard. New features includes:</p><ul><li>Transactions and Reports</li><li>User management</li><li>Service management</li></ul><h4 id="tech-stack-3" tabindex="-1">Tech stack <a class="header-anchor" href="#tech-stack-3" aria-label="Permalink to &quot;Tech stack&quot;">​</a></h4><ul><li>Vue.js</li><li>WebdriverIO</li><li>AWS Cognito</li><li>AWS Api Gateway, Lambda, DynamoDB</li><li>GitLab CI/CD</li></ul><h3 id="continuous-improvement-to-payto-api" tabindex="-1">Continuous improvement to PayTo API <a class="header-anchor" href="#continuous-improvement-to-payto-api" aria-label="Permalink to &quot;Continuous improvement to PayTo API&quot;">​</a></h3><h2 id="_2022" tabindex="-1">2022 <a class="header-anchor" href="#_2022" aria-label="Permalink to &quot;2022&quot;">​</a></h2><h3 id="payto-api" tabindex="-1">PayTo API <a class="header-anchor" href="#payto-api" aria-label="Permalink to &quot;PayTo API&quot;">​</a></h3><p>Design and development of PayTo API.</p><p>The API includes capabilities to:</p><ul><li>Create PayTo agreement</li><li>Amend agreement</li><li>Cancel agreement</li><li>Initiate payment for PayTo agreement</li></ul><h4 id="tech-stack-4" tabindex="-1">Tech stack <a class="header-anchor" href="#tech-stack-4" aria-label="Permalink to &quot;Tech stack&quot;">​</a></h4><ul><li>AWS API Gateway, Lambda, DynamoDB, SQS (AWS Serverless stack)</li><li>AWS CloudFormation</li><li>GitLab CI/CD</li></ul><h3 id="automate-user-migration-when-user-log-in" tabindex="-1">Automate user migration when user log-in <a class="header-anchor" href="#automate-user-migration-when-user-log-in" aria-label="Permalink to &quot;Automate user migration when user log-in&quot;">​</a></h3><ul><li><p>Automate migration of user accounts to a new Cognito user pool.</p></li><li><p>This is achieved by utilising Lambda triggers attached to Cognito user pool.</p></li></ul><h4 id="tech-stack-5" tabindex="-1">Tech stack <a class="header-anchor" href="#tech-stack-5" aria-label="Permalink to &quot;Tech stack&quot;">​</a></h4><ul><li>AWS Cognito</li><li>AWS Lambda</li></ul>',40)]))}const p=e(o,[["render",r]]);export{m as __pageData,p as default};
